<?phpdefined('BASEPATH') OR exit('No direct script access allowed');?><div class="container clearfix">    <!-- Portfolio Filter    ============================================= -->    <ul id="portfolio-filter" class="clearfix">        <li class="activeFilter"><a href="#" data-filter="*">Show All</a></li>        <!--        <li><a href="#" data-filter=".pf-icons">Books My Friends Own</a></li>-->        <!--        <li><a href="#" data-filter=".pf-illustrations">Books I Can Borrow Now</a></li>-->        <!--        <li><a href="#" data-filter=".pf-uielements">Books Friends Borrowed In The Past</a></li>-->    </ul><!-- #portfolio-filter end -->    <div id="portfolio-shuffle">        <i class="icon-random"></i>    </div>    <div class="clear"></div>    <!-- Portfolio Items    ============================================= -->    <div id="portfolio" class="portfolio-6 portfolio-masonry clearfix">        <?php for ($i = 0;                   $i < count($books);                   $i++): ?>            <article class="portfolio-item pf-media pf-icons"                     id="ArticleIdBook<?php echo $books[$i]->google_id; ?>">                <div class="portfolio-image" style="box-shadow: 3px 1px 10px #888888;">                    <a href="page/book/<?php echo $books[$i]->id; ?>">                        <img                            src="https://books.google.com/books?id=<?php echo $books[$i]->google_id; ?>&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api"                            alt="Open Imagination">                    </a>                </div>                <div class="portfolio-desc center">                    <h3><a href="page/book/<?php echo $books[$i]->id; ?>"><?php echo $books[$i]->name; ?></a></h3>                    <span><a href="page/book/<?php echo $books[$i]->id; ?>"><?php echo $books[$i]->author; ?></a></span>                    <h5>                        <?php if (isset($books[$i]->reviews)) {                            echo '<a href="page/book/' . $books[$i]->id . '">';                            $bookRatingAvg = 0;                            $reviewCount = 1;                            for ($k = 0;                                 $k < count($books[$i]->reviews);                                 $k++) {                                $bookRatingAvg = $bookRatingAvg + ($books[$i]->reviews[$k]->rating - $bookRatingAvg) / $reviewCount;                                $reviewCount++;                            }                            for ($stars = 0;                                 $stars < 5;                                 $stars++) {                                if ($bookRatingAvg - $stars >= 1.0) {                                    echo '<i class="icon-star3"></i>';                                } else if ($bookRatingAvg - $stars >= 0.5) {                                    echo '<i class="icon-star-half-full"></i>';                                } else {                                    echo '<i class="icon-star-empty"></i>';                                }                            }                            echo '<p style="display: inline !important;">&nbsp;' . count($books[$i]->reviews) . '</p>';                            echo '</a>';                        }                        ?>                    </h5>                    <form class="nobottommargin" action="page/loanBook" method="post"                          name="loanFormNum<?php echo $i; ?>">                        <input type="hidden" value="<?php echo $books[$i]->google_id; ?>"                               name="bookGoogleId">                        <input type="hidden" value="<?php echo $friendid; ?>"                               name="ownerUserId">                        <button type="submit" style="width: 100%;"                                class="button button-primary button-sm nomargin">                            <i class="icon-line2-star"></i>&nbsp;Request Loan                        </button>                    </form>                </div>            </article>            <?php        endfor;        ?>    </div><!-- #portfolio end -->    <!-- Portfolio Script    ============================================= -->    <script type="text/javascript">        jQuery(window).load(function () {            var $container = $('#portfolio');            $container.isotope({transitionDuration: '0.65s'});            $('#portfolio-filter a').click(function () {                $('#portfolio-filter li').removeClass('activeFilter');                $(this).parent('li').addClass('activeFilter');                var selector = $(this).attr('data-filter');                $container.isotope({filter: selector});                return false;            });            $('#portfolio-shuffle').click(function () {                $container.isotope('updateSortData').isotope({                    sortBy: 'random'                });            });            $(window).resize(function () {                $container.isotope('layout');            });        });    </script><!-- Portfolio Script End --></div>